import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2';

const Pro_list = () => {

  const [profile, setProfile] = useState([])
  const fetchProfiles = async () => {
    const res = await fetch('http://localhost:5000/company/getall');
    console.log(res.status)

    const data = await res.json();
    console.log(data);
    setProfile(data)
  }

  useEffect(() => {
    fetchProfiles();
  }, [])

  const deleteProfile = async (id) => {
    console.log(id);
    const res = await fetch('http://localhost:5000/company/delete/' + id, {
      method: "DELETE",
    })
    console.log(res.status);
    if (res.status === 200) {
      fetchProfiles();
      Swal.fire({
        icon: 'success',
        title: 'Success',
        text: 'Profile Deleted Successfully'
      })
    }
  }

  return (
    <div className='container'>
      <h1 className='text-center mt-3'>ALL PROFILES HERE</h1>
      {profile.map((profiles) => (
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">{profiles.name}</h5>
            <div>
              <p>
                <label htmlFor="">Name</label>{profiles.type}
              </p>
            </div>
            <div>
              <p>
                {profiles.departments}
              </p>
            </div>
            <div>
              <p>
                {profiles.working}
              </p>
            </div>


            {/* <div>{star.map(() => <i className="fas fa-star" style={{color:'green'}}></i>)}</div> */}




            <button type="button" className="btn btn-danger" onClick={() => (deleteProfile(profiles._id))}>
              Delete Profile
            </button>
          </div>
        </div>
      ))}

      <Link to='/home'><b>Back</b></Link>

    </div>
  )
}

export default Pro_list
